// Xá»­ lÃ½ attachments tá»« base64 (bao gá»“m inline attachments vá»›i CID)
// Format cho n8n Email Send node (nodemailer)
// Náº¿u CID khÃ´ng hoáº¡t Ä‘á»™ng, sáº½ embed base64 vÃ o HTML
const inputData = $input.item.json;
let htmlContent = inputData.html_content || inputData.body?.html_content || '';
const attachments = inputData.attachments || inputData.body?.attachments || [];
const processedAttachments = [];

console.log('ðŸ“Ž Total attachments to process:', attachments.length);

// Kiá»ƒm tra náº¿u khÃ´ng cÃ³ attachments
if (!attachments || attachments.length === 0) {
  console.log('â„¹ï¸ No attachments to process');
  return {
    ...inputData,
    processedAttachments: []
  };
}

for (const att of attachments) {
  try {
    // Kiá»ƒm tra content cÃ³ tá»“n táº¡i khÃ´ng
    if (!att.content) {
      console.warn(`âš ï¸ Attachment ${att.name || 'unknown'} has no content, skipping`);
      continue;
    }

    // Náº¿u lÃ  inline attachment vá»›i CID, cÃ³ 2 cÃ¡ch:
    // 1. Gá»­i nhÆ° inline attachment (Æ°u tiÃªn)
    // 2. Embed base64 vÃ o HTML (fallback náº¿u CID khÃ´ng hoáº¡t Ä‘á»™ng)
    if (att.inline && att.cid) {
      // Thá»­ decode base64 Ä‘á»ƒ kiá»ƒm tra
      let buffer;
      try {
        buffer = Buffer.from(att.content, 'base64');
      } catch (decodeError) {
        console.error(`âŒ Failed to decode base64 for ${att.name || 'unknown'}:`, decodeError.message);
        continue;
      }

      if (!buffer || buffer.length === 0) {
        console.warn(`âš ï¸ Attachment ${att.name || 'unknown'} has empty buffer, skipping`);
        continue;
      }

      // Táº¡o data URI Ä‘á»ƒ embed vÃ o HTML (fallback)
      const dataUri = `data:${att.mimeType || 'image/jpeg'};base64,${att.content}`;

      // Replace CID trong HTML vá»›i data URI (Ä‘áº£m báº£o logo hiá»ƒn thá»‹)
      // Giá»¯ cáº£ hai: gá»­i attachment VÃ€ embed vÃ o HTML
      const cidPattern = new RegExp(`cid:${att.cid.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}`, 'gi');
      if (cidPattern.test(htmlContent)) {
        htmlContent = htmlContent.replace(cidPattern, dataUri);
        console.log(`ðŸ“· Replaced CID ${att.cid} with base64 data URI in HTML`);
      }

      // Váº«n thÃªm vÃ o attachments Ä‘á»ƒ thá»­ gá»­i nhÆ° inline attachment
      const attachmentObj = {
        filename: att.name || 'attachment',
        content: buffer,
        contentType: att.mimeType || 'application/octet-stream',
        cid: att.cid // Thá»­ vá»›i CID
      };

      processedAttachments.push(attachmentObj);
      console.log(`ðŸ“· Added inline attachment with CID: ${att.cid} (${att.name || 'unknown'})`);
    } else {
      // Regular attachment
      let buffer;
      try {
        buffer = Buffer.from(att.content, 'base64');
      } catch (decodeError) {
        console.error(`âŒ Failed to decode base64 for ${att.name || 'unknown'}:`, decodeError.message);
        continue;
      }

      if (!buffer || buffer.length === 0) {
        console.warn(`âš ï¸ Attachment ${att.name || 'unknown'} has empty buffer, skipping`);
        continue;
      }

      const attachmentObj = {
        filename: att.name || 'attachment',
        content: buffer,
        contentType: att.mimeType || 'application/octet-stream'
      };

      processedAttachments.push(attachmentObj);
      console.log(`ðŸ“Ž Added regular attachment: ${att.name || 'unknown'}`);
    }
  } catch (error) {
    console.error(`âŒ Error processing attachment ${att.name || 'unknown'}:`, error.message);
  }
}

console.log(`âœ… Processed ${processedAttachments.length} out of ${attachments.length} attachments`);

// Log Ä‘á»ƒ debug
if (processedAttachments.length > 0) {
  processedAttachments.forEach((att, idx) => {
    console.log(`  [${idx}] ${att.filename} - CID: ${att.cid || 'none'} - Size: ${att.content.length} bytes`);
  });
}

return {
  ...inputData,
  html_content: htmlContent, // HTML Ä‘Ã£ Ä‘Æ°á»£c update vá»›i data URI
  processedAttachments: processedAttachments
};
