// X·ª≠ l√Ω attachments t·ª´ base64 (bao g·ªìm inline attachments v·ªõi CID)
// Format cho n8n Email Send node (nodemailer)
// N·∫øu CID kh√¥ng ho·∫°t ƒë·ªông, s·∫Ω embed base64 v√†o HTML
const inputData = $input.item.json;

// Debug: Log to√†n b·ªô inputData ƒë·ªÉ xem c·∫•u tr√∫c
console.log('üîç Input data keys:', Object.keys(inputData));
console.log('üîç Input data structure:', JSON.stringify(inputData, null, 2).substring(0, 500));

// T√¨m attachments ·ªü nhi·ªÅu n∆°i
let htmlContent = inputData.html_content || inputData.body?.html_content || '';

// Lo·∫°i b·ªè \n ·ªü ƒë·∫ßu HTML n·∫øu c√≥
if (htmlContent && htmlContent.startsWith('\n')) {
  htmlContent = htmlContent.substring(1).trim();
  console.log('üßπ Removed leading \n from HTML');
}

let attachments = inputData.attachments || inputData.body?.attachments || [];

// N·∫øu kh√¥ng t√¨m th·∫•y, th·ª≠ t√¨m t·ª´ c√°c v·ªã tr√≠ kh√°c
if (!attachments || attachments.length === 0) {
  console.log('‚ö†Ô∏è Attachments not found in default locations, trying alternatives...');
  // Th·ª≠ t√¨m t·ª´ body tr·ª±c ti·∫øp
  if (inputData.body && Array.isArray(inputData.body.attachments)) {
    attachments = inputData.body.attachments;
    console.log('‚úÖ Found attachments in inputData.body.attachments');
  }
  // Th·ª≠ t√¨m t·ª´ root level
  else if (Array.isArray(inputData.attachments)) {
    attachments = inputData.attachments;
    console.log('‚úÖ Found attachments in inputData.attachments');
  }
}

const processedAttachments = [];

console.log('üìé Total attachments to process:', attachments.length);

// Ki·ªÉm tra n·∫øu kh√¥ng c√≥ attachments
if (!attachments || attachments.length === 0) {
  console.log('‚ÑπÔ∏è No attachments to process');
  return {
    ...inputData,
    processedAttachments: []
  };
}

for (const att of attachments) {
  try {
    // Ki·ªÉm tra content c√≥ t·ªìn t·∫°i kh√¥ng
    if (!att.content) {
      console.warn(`‚ö†Ô∏è Attachment ${att.name || 'unknown'} has no content, skipping`);
      continue;
    }

    // N·∫øu l√† inline attachment v·ªõi CID, c√≥ 2 c√°ch:
    // 1. G·ª≠i nh∆∞ inline attachment (∆∞u ti√™n)
    // 2. Embed base64 v√†o HTML (fallback n·∫øu CID kh√¥ng ho·∫°t ƒë·ªông)
    if (att.inline && att.cid) {
      // Th·ª≠ decode base64 ƒë·ªÉ ki·ªÉm tra
      let buffer;
      try {
        buffer = Buffer.from(att.content, 'base64');
      } catch (decodeError) {
        console.error(`‚ùå Failed to decode base64 for ${att.name || 'unknown'}:`, decodeError.message);
        continue;
      }

      if (!buffer || buffer.length === 0) {
        console.warn(`‚ö†Ô∏è Attachment ${att.name || 'unknown'} has empty buffer, skipping`);
        continue;
      }

      // T·∫°o data URI ƒë·ªÉ embed v√†o HTML (fallback)
      const dataUri = `data:${att.mimeType || 'image/jpeg'};base64,${att.content}`;

      // Replace CID trong HTML v·ªõi data URI (ƒë·∫£m b·∫£o logo hi·ªÉn th·ªã)
      // Gi·ªØ c·∫£ hai: g·ª≠i attachment V√Ä embed v√†o HTML
      const cidPattern = new RegExp(`cid:${att.cid.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}`, 'gi');
      if (cidPattern.test(htmlContent)) {
        const beforeReplace = htmlContent.substring(0, 200);
        htmlContent = htmlContent.replace(cidPattern, dataUri);
        const afterReplace = htmlContent.substring(0, 200);
        console.log(`üì∑ Replaced CID ${att.cid} with base64 data URI in HTML`);
        console.log(`   Before: ${beforeReplace}...`);
        console.log(`   After: ${afterReplace}...`);
        console.log(`   Data URI length: ${dataUri.length} chars`);
      } else {
        console.warn(`‚ö†Ô∏è CID ${att.cid} not found in HTML content`);
        console.log(`   HTML preview: ${htmlContent.substring(0, 300)}...`);
      }

      // V·∫´n th√™m v√†o attachments ƒë·ªÉ th·ª≠ g·ª≠i nh∆∞ inline attachment
      const attachmentObj = {
        filename: att.name || 'attachment',
        content: buffer,
        contentType: att.mimeType || 'application/octet-stream',
        cid: att.cid // Th·ª≠ v·ªõi CID
      };

      processedAttachments.push(attachmentObj);
      console.log(`üì∑ Added inline attachment with CID: ${att.cid} (${att.name || 'unknown'})`);
    } else {
      // Regular attachment
      let buffer;
      try {
        buffer = Buffer.from(att.content, 'base64');
      } catch (decodeError) {
        console.error(`‚ùå Failed to decode base64 for ${att.name || 'unknown'}:`, decodeError.message);
        continue;
      }

      if (!buffer || buffer.length === 0) {
        console.warn(`‚ö†Ô∏è Attachment ${att.name || 'unknown'} has empty buffer, skipping`);
        continue;
      }

      const attachmentObj = {
        filename: att.name || 'attachment',
        content: buffer,
        contentType: att.mimeType || 'application/octet-stream'
      };

      processedAttachments.push(attachmentObj);
      console.log(`üìé Added regular attachment: ${att.name || 'unknown'}`);
    }
  } catch (error) {
    console.error(`‚ùå Error processing attachment ${att.name || 'unknown'}:`, error.message);
  }
}

console.log(`‚úÖ Processed ${processedAttachments.length} out of ${attachments.length} attachments`);

// Log ƒë·ªÉ debug
if (processedAttachments.length > 0) {
  processedAttachments.forEach((att, idx) => {
    console.log(`  [${idx}] ${att.filename} - CID: ${att.cid || 'none'} - Size: ${att.content.length} bytes`);
  });
}

return {
  ...inputData,
  html_content: htmlContent, // HTML ƒë√£ ƒë∆∞·ª£c update v·ªõi data URI
  processedAttachments: processedAttachments
};
